import React from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import IconButton from '@material-ui/core/IconButton';
import Typography from '@material-ui/core/Typography';
import CommentIcon from '@material-ui/icons/Comment';
import {ExpansionPanel,ExpansionPanelSummary,ExpansionPanelDetails} from '@material-ui/core';
import {List, ListItem,ListItemText, ListItemSecondaryAction,Checkbox} from '@material-ui/core';
import {InputLabel, FormControl,Button,TextField,Select} from '@material-ui/core';
import Grid from '@material-ui/core/Grid';
import {Paper} from '@material-ui/core/Paper';

const styles = theme => ({
  flex: {
    flex: 1,
  },
  paper: {
    padding: theme.spacing.unit * 2,
    textAlign: 'center',
    color: theme.palette.text.secondary,
  },
  grid: {
      textAlign: 'center'
  },
  grid2: {
      textAlign: 'left'
  },
  textField: {
      marginLeft: theme.spacing.unit,
      marginRight: theme.spacing.unit,
      width: 200,
    },
    dense: {
      marginTop: 19,
  },
});

class MaintainPanel extends React.Component {
    state = {
        open: false,
        maintain: [],
        expanded: 'panel-maintain',
        checked: [0]
    }
    handleChange = name => (event,expanded) => {
        this.setState({ [name]: event.target.value });
        if(typeof expanded != 'undefined') {
            this.setState({expanded: expanded ? 'panel-maintain' : false});
        }
        alert("EVENT: " + JSON.stringify(event))
        alert("EXPANDED: " + expanded)
    };

    handleToggle = value => () => {
        const { checked } = this.state;
        const currentIndex = checked.indexOf(value);
        const newChecked = [...checked];

        if (currentIndex === -1) {
          newChecked.push(value);
        } else {
          newChecked.splice(currentIndex, 1);
        }
        this.setState({
          checked: newChecked,
        });
    }

    render() {
        const { classes } = this.props;
        const { expanded } = this.state;

        return(
                <ExpansionPanel
                square
                expanded={expanded === 'panel-maintain'}
                onChange={this.handleChange('panel-maintain')}
                >
                    <ExpansionPanelSummary>
                        <Typography>Maintain</Typography>
                    </ExpansionPanelSummary>
                    <ExpansionPanelDetails>
                        <form className={classes.container} noValidate autoComplete="off">
                        <Grid container className={styles.grid} spacing={24}>
                            <Grid item xs={3}>
                                <TextField
                                    id="maintain-name"
                                    label="Name"
                                    className={classes.TextField}
                                    value={this.state.name}
                                    onChange={this.handleChange('name')}
                                    margin="normal"
                                    />
                            </Grid>
                            <Grid item xs={6}>
                                grid 2
                            </Grid>
                            <Grid item xs={3}>
                                grid 3
                            </Grid>
                            <Grid item xs={3}>
                                grid 4
                            </Grid>
                            <Grid item xs={3}>
                                grid 5
                            </Grid>

                        </Grid>
                    </form>
                    </ExpansionPanelDetails>
                </ExpansionPanel>
        )
    }
}

export default withStyles(styles)(MaintainPanel);
