import React from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import Dialog from '@material-ui/core/Dialog';
import ListItemText from '@material-ui/core/ListItemText';
import ListItem from '@material-ui/core/ListItem';
import List from '@material-ui/core/List';
import Divider from '@material-ui/core/Divider';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import IconButton from '@material-ui/core/IconButton';
import Typography from '@material-ui/core/Typography';
import CloseIcon from '@material-ui/icons/Close';
import Slide from '@material-ui/core/Slide';
import {Button} from '@material-ui/core';
import axios from 'axios';

const styles = {
  appBar: {
    position: 'relative',
  },
  flex: {
    flex: 1,
  },
};

function Transition(props) {
  return <Slide direction="up" {...props} />;
}

class MaintainDialog extends React.Component {
  state = {
    open: false,
  };

  constructor () {
      super();

      this.getMaintain(this.props.id) ;
  }

  handleClickOpen = () => {
    this.setState({ open: true });
  };

  handleClose = () => {
    this.setState({ open: false });
  };

  getMaintain(id) {
      let query = `query{
        maintain(id:`+id+`) {
          name,
          todos {
            id,
            name,
            parts{
              id,
              name
            }
          }
        }
      }`;

      axios({
          url: 'http://localhost:5000/graphql',
          method: 'post',
          data: {
              query: query
          }
      })
      /*
      axios.post("http://localhost:5000/graphql", { withCredentials: true,
                                                    crossdomain: true,
                                                    rejectUnauthorized: false,

          })
      */
              .then( (response) => {
                  console.log("get maintain response: " + JSON.stringify(response));
                  console.log("response header: " + JSON.stringify(response.headers));
                  this.setState({maintains: response.data});

                  // User logged in?
                  if (this.state.maintains.status) {
                      console.log("user status is : " + this.state.maintains.status);
                      throw( new Error("User not logged in"));
                  }

                  console.log("MAINTAINS--- " + JSON.stringify(this.state.maintains));
              })
              .catch( (err) => {
                  console.log("err maintain response: " + JSON.stringify(err));
              })
      }

  render() {
    const { classes } = this.props;
    return (
      <div>
        <Button variant="outlined" color="primary" onClick={this.handleClickOpen}>
          Open full-screen dialog
        </Button>
        <Dialog
          fullScreen
          open={this.state.open}
          onClose={this.handleClose}
          TransitionComponent={Transition}
        >

          <AppBar className={classes.appBar}>
            <Toolbar>
              <IconButton color="inherit" onClick={this.handleClose} aria-label="Close">
                <CloseIcon />
              </IconButton>
              <Typography variant="h6" color="inherit" className={classes.flex}>
                Sound
              </Typography>
              <Button color="inherit" onClick={this.handleClose}>
                save
              </Button>
            </Toolbar>
          </AppBar>
          <List>
            <ListItem button>
              <ListItemText primary="Phone ringtone" secondary="Titania" />
            </ListItem>
            <Divider />
            <ListItem button>
              <ListItemText primary="Default notification ringtone" secondary="Tethys" />
            </ListItem>
            <ListItem button>
                <ListItemText primary="ID value passed in?" secondary={this.props.maintainID} />
              </ListItem>
          </List>
        </Dialog>
      </div>
    );
  }
}

MaintainDialog.propTypes = {
  classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(MaintainDialog);
