import  React, {Component } from 'react'
import Card from '@material-ui/core/Card'
import CardActions from '@material-ui/core/CardActions'
import CardContent from '@material-ui/core/CardContent'
import Button from '@material-ui/core/Button'
import Typography from '@material-ui/core/Typography'
import MaintainDialog from './MaintainDialog';


class Maintain extends Component {
        state = {
            maintains: [],
            searchFor: '',
        }

        constructor() {
            super();
            this.getMaintains();
        }

//    console.log(props)
        render() {
            return(
                <div>
                    { props.maintain ? (
                        <Card >
                            <CardContent>
                                <Typography gutterBottom variant="headline" component="h2">
                                    {props.maintain.name}
                                </Typography>
                                <Typography component="p">
                                    {props.maintain.description}
                                </Typography>
                                <Typography component="p">
                                    {props.maintain.part_nbr}
                                </Typography>
                                <Typography component="p">
                                    {props.maintain.status}
                                </Typography>
                            </CardContent>
                            <CardActions>
                                <MaintainDialog
                                  selectedValue={this.state.selectedValue}
                                  open={this.state.open}
                                  onClose={this.handleClose}
                                />

                                <Button size="small" color="primary" href={"#" +props.maintain.id} target="_blank">
                                    Details
                                </Button>
                            </CardActions>
                        </Card>
                    ) : null}
                </div>
            )
        }
    }

export default Maintain
