import React from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import Dialog from '@material-ui/core/Dialog';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import IconButton from '@material-ui/core/IconButton';
import Typography from '@material-ui/core/Typography';
import CloseIcon from '@material-ui/icons/Close';
import Slide from '@material-ui/core/Slide';
import {InputLabel, FormControl,Button,TextField,Select,MenuItem,Input} from '@material-ui/core';
import axios from 'axios';

const styles = {
  appBar: {
    position: 'relative',
  },
  flex: {
    flex: 1,
  },
};

function Transition(props) {
  return <Slide direction="up" {...props} />;
}

class MaintainDialog extends React.Component {
  state = {
    open: false,
    maintain: []
  };

    componentDidMount() {
        console.log("componentDidMount ID: " + this.props.maintainID)
        this.getMaintain(this.props.maintainID);
    }

  handleClickOpen = () => {
      console.log("handleClickOpen ID: " + this.props.maintainID)
    this.setState({ open: true });
  };

  handleClose = () => {
    this.setState({ open: false });
  };

  getMaintain(id) {
      let query = `query{
        maintain(id:`+id+`) {
          name,
          todos {
            id,
            name,
            parts{
              id,
              name
            }
          }
        }
      }`;

      axios({
          url: 'http://localhost:5000/toto/graphql',
          method: 'post',
          data: {
              query: query
          }
      })
      /*
      axios.post("http://localhost:5000/graphql", { withCredentials: true,
                                                    crossdomain: true,
                                                    rejectUnauthorized: false,

          })
      */
              .then( (response) => {
                  console.log("get maintain response: " + JSON.stringify(response));
                  console.log("response header: " + JSON.stringify(response.headers));
                  console.log("******************************** set state maintain data ************************")
                  console.log("response.data: " + JSON.stringify(response.data["data"]["maintain"]));
                  console.log("*********************************************************************************");
                  this.setState({maintain: response.data["data"]["maintain"]});
                  // User logged in?
                  if (this.state.maintain.status) {
                      console.log("user status is : " + this.state.maintain.status);
                      throw( new Error("User not logged in"));
                  }
                 console.log("STATE MAINTAIN: " + JSON.stringify(this.state.maintain));
                  console.log("MAINTAIN OBJECT ===== " + JSON.stringify(this.state.maintain.data.maintain));
                  console.log("MAINTAIN OBJECT NAME ===== " + JSON.stringify(this.state.maintain.data.maintain.name));
              })
              .catch( (err) => {
                  console.log("err maintain response: " + JSON.stringify(err));
              })
      }

      handleChange = event => {
         this.setState({ [event.target.name]: event.target.value });
     }

  render() {
    const { classes } = this.props;
    return (
      <div>
        <Button variant="outlined" color="primary" onClick={this.handleClickOpen}>
          Open full-screen dialog
        </Button>
        <Dialog
          fullScreen
          open={this.state.open}
          onClose={this.handleClose}
          TransitionComponent={Transition}
        >

          <AppBar className={classes.appBar}>
            <Toolbar>
              <IconButton color="inherit" onClick={this.handleClose} aria-label="Close">
                <CloseIcon />
              </IconButton>
              <Typography variant="h6" color="inherit" className={classes.flex}>
                  {this.state.maintain["name"]}
              </Typography>
              <Button color="inherit" onClick={this.handleClose}>
                save
              </Button>
            </Toolbar>
          </AppBar>
          <form className={classes.root} autoComplete="off">
        <FormControl className={classes.formControl}>
<InputLabel htmlFor="age-simple">Age</InputLabel>
           <TextField id="maintain-name"
                      label="Maintain Project"
                      className={classes.textField}
                      value={this.state.maintain["name"]}
                      margin="normal"
            /> {/*<!-- TextField -->*/}
            <TextField id="maintain-part-nbr"
                       label="Maintain Project"
                       className={classes.textField}
                       value={this.state.maintain["part_nbr"]}
                       margin="normal"
             /> {/*<!-- TextField -->*/}
            <TextField id="maintain-description"
                       label="Description"
                       multiline
                       className={classes.textField}
                       value={this.state.maintain["description"]}
                       margin="normal"
             /> {/*<!-- TextField -->*/}
            <Select
                        value={this.state.age}
                        onChange={this.handleChange}
                        inputProps={{
                          name: 'age',
                          id: 'age-simple',
                        }}
                      >
                        <MenuItem value="">
                          <em>None</em>
                        </MenuItem>
                        <MenuItem value={10}>Ten</MenuItem>
                        <MenuItem value={20}>Twenty</MenuItem>
                        <MenuItem value={30}>Thirty</MenuItem>
                      </Select>

                      <Select
                         value={this.state.maintain["status"]}
                         onChange={this.handleChange}
                         input={<Input name="maintain-status" id="maintain-status" />}
                         name="select-maintain-status"
                       >
                             <MenuItem value="">
                               <em>none</em>
                             </MenuItem>
                             <MenuItem value={'active'}>Active</MenuItem>
                             <MenuItem value={'inactive'}>Inactive</MenuItem>
                     </Select>  {/*<!-- Select -->*/}
            </FormControl>
        </form>
        </Dialog>
      </div>
    );
  }
}

MaintainDialog.propTypes = {
  classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(MaintainDialog);
