import React from 'react';
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogContentText from '@material-ui/core/DialogContentText';
import DialogTitle from '@material-ui/core/DialogTitle';

import doLogin from './MaintainData';

export default class FormDialog extends React.Component {
  state = {
    open: false,
  };

  handleClickOpen = () => {
    this.setState({ open: true });
  };

  handleClose = () => {
    this.setState({ open: false });
  };

  handleChange = name => (event) => {
      this.setState({ [name]: event.target.value });
  };

  handleLogin = (e) => {
      e.preventDefault();
      alert("username: " + this.state.username);
      alert("handling Login: " + doLogin(this.state.username,this.state.password));
      /*
     // axios
    doLogin(this.state.username,this.state.password)
    // if success - change button to username
    .then( (results) => {
        console.log("MaintainData:RESULTS: " + JSON.stringify(results));
    })
    */

    // if fail, present alert
  }

  render() {
    return (
      <div>
        <Button variant="outlined" color="red" onClick={this.handleClickOpen}>
          Login
        </Button>

        <Dialog
          open={this.state.open}
          onClose={this.handleClose}
          aria-labelledby="form-dialog-title"
        >
          <DialogTitle id="form-dialog-title">Login</DialogTitle>
          <DialogContent>
            <DialogContentText>
              For each wrong password given is one child we will take and feed to the hounds.
            </DialogContentText>
            <form action="">

            <TextField
              autoFocus
              margin="dense"
              id="username"
              label="User Name"
              fullWidth
            />
            <TextField
              autoFocus
              margin="dense"
              id="password"
              label="Password"
              type="password"
              fullWidth
            />
            </form>
          </DialogContent>
          <DialogActions>
            <Button onClick={this.handleClose} color="primary">
              Cancel
            </Button>
            <Button onClick={this.handleLogin} color="primary">
              Login
            </Button>
          </DialogActions>
        </Dialog>
      </div>
    );
  }
}
